import React, { useState, useEffect, ChangeEvent } from 'react';
import * as s from './splitStyle';

const Split: React.FC = () => {
  const fixedMinTime = 0; // 이 값을 실제 값으로 초기화해야 합니다.
  const fixedMaxTime = 100; // 이 값을 실제 값으로 초기화해야 합니다.
  const timeGap = 10; // 이 값을 실제 값으로 초기화해야 합니다.

  const [rangeMinValue, setRangeMinValue] = useState<number>(fixedMinTime);
  const [rangeMaxValue, setRangeMaxValue] = useState<number>(fixedMaxTime);
  const [rangeMinPercent, setRangeMinPercent] = useState<number>(0);
  const [rangeMaxPercent, setRangeMaxPercent] = useState<number>(100);

  const prcieRangeMinValueHandler = (e: ChangeEvent<HTMLInputElement>) => {
    setRangeMinValue(parseInt(e.target.value));
  };

  const prcieRangeMaxValueHandler = (e: ChangeEvent<HTMLInputElement>) => {
    setRangeMaxValue(parseInt(e.target.value));
  };

  const twoRangeHandler = () => {
    if (rangeMaxValue - rangeMinValue < timeGap) {
      setRangeMaxValue(rangeMinValue + timeGap);
      setRangeMinValue(rangeMaxValue - timeGap);
    } else {
      setRangeMinPercent((rangeMinValue / fixedMaxTime) * 100);
      setRangeMaxPercent(100 - (rangeMaxValue / fixedMaxTime) * 100);
    }
  };

  useEffect(() => {
    twoRangeHandler();
  }, [rangeMinValue, rangeMaxValue]);

  return (
    <div style={{ padding: '50px' }}>
      <s.FilterTimeSlide>
        {/*         <s.FilterTimeSlideInner
          rangeMinPercent={rangeMinPercent}
          rangeMaxPercent={rangeMaxPercent}
        /> */}
      </s.FilterTimeSlide>
      <s.FilterTimeRangeWrap>
        <s.FilterTimeRangeMin
          type="range"
          min={fixedMinTime}
          max={fixedMaxTime - timeGap}
          step="1000"
          value={rangeMinValue}
          onChange={prcieRangeMinValueHandler}
        />
        <s.FilterTimeRangeMax
          type="range"
          min={fixedMinTime + timeGap}
          max={fixedMaxTime}
          step="1000"
          value={rangeMaxValue}
          onChange={prcieRangeMaxValueHandler}
        />
      </s.FilterTimeRangeWrap>
    </div>
  );
};

export default Split;
