import React, { useState } from 'react';
import './split.scss';

const RangeSlider: React.FC = () => {
  const [startTime, setStartTime] = useState<number>(0);
  const [endTime, setEndTime] = useState<number>(100);
  const maxTime = 24; // 최대 시간 설정

  const handleSliderChange = (
    event: React.ChangeEvent<HTMLInputElement>,
    isStartTime: boolean,
  ) => {
    const value = parseFloat(event.target.value);

    if (!isNaN(value)) {
      if (isStartTime) {
        setStartTime(value);
      } else {
        setEndTime(value);
      }
    }
  };

  return (
    <div className="range-slider">
      <div className="slider-input">
        <label>시작 시간:</label>
        <input
          type="range"
          min={0}
          max={maxTime}
          step={0.01}
          value={startTime}
          onChange={(e) => handleSliderChange(e, true)}
        />
        <span>{startTime.toFixed(2)}</span>
      </div>
      <div className="slider-input">
        <label>종료 시간:</label>
        <input
          type="range"
          min={0}
          max={maxTime}
          step={0.01}
          value={endTime}
          onChange={(e) => handleSliderChange(e, false)}
        />
        <span>{endTime.toFixed(2)}</span>
      </div>
    </div>
  );
};

export default RangeSlider;
