import React, { useState } from 'react';
import './split.scss'; // 필요한 SCSS 파일을 가져옵니다.

function RangeSlider({
  min,
  max,
  step,
  value,
  prefix,
  suffix,
}: {
  min: number;
  max: number;
  step: number;
  value: number;
  prefix?: string;
  suffix?: string;
}) {
  const [sliderValue, setSliderValue] = useState(value);

  const handleSliderChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = parseInt(event.target.value);
    setSliderValue(newValue);
  };

  return (
    <div
      className="range-slider"
      style={{
        '--min': min,
        '--max': max,
        '--step': step,
        '--value': sliderValue,
        '--text-value': JSON.stringify(sliderValue),
        '--prefix': prefix || '',
        '--suffix': suffix || '',
      }}
    >
      <input
        type="range"
        min={min}
        max={max}
        step={step}
        value={sliderValue}
        onChange={handleSliderChange}
      />
      <output />
      <div className="range-slider__progress" />
    </div>
  );
}

export default function App() {
  return (
    <div>
      <RangeSlider min={0} max={100} step={5} value={75} />
      <RangeSlider min={0} max={10000} step={100} value={200} prefix="$" />
      <RangeSlider min={-500} max={500} valueA={-220} valueB={400} suffix="%" />
      <RangeSlider min={0} max={10} valueA={2} valueB={5} />
    </div>
  );
}
