import React, { useEffect, useState } from 'react';
import './split.scss'; // SCSS 파일을 가져옵니다.

function InputRange() {
  const [sliderValue, setSliderValue] = useState<[number, number]>([15, 85]); // sliderValue의 타입을 명시
  const min = 100.23; // min의 타입을 명시
  const max = 1021.23; // max의 타입을 명시

  useEffect(() => {
    const slider = document.querySelector<HTMLInputElement>('#slider');
    const lowDollarAmount =
      document.querySelector<HTMLDivElement>('.lowDollarAmount');
    const highDollarAmount =
      document.querySelector<HTMLDivElement>('.highDollarAmount');

    if (slider && lowDollarAmount && highDollarAmount) {
      slider.addEventListener('change', () => {
        const low = min + (max - min) * (sliderValue[0] / 100);
        const high = min + (max - min) * (sliderValue[1] / 100);
        lowDollarAmount.textContent = formatPrice(low);
        highDollarAmount.textContent = formatPrice(high);
      });
    }
  }, [min, max, sliderValue]);

  const formatPrice = (price: number): string => {
    if (price !== 0) {
      const formattedPrice =
        '$' +
        parseFloat(price.toFixed(2))
          .toString()
          .replace(/(\d+)(?=(\d{3})+\.?)/g, '$1,');
      return formattedPrice;
    }
    return '';
  };

  return (
    <div className="input-range">
      <input id="slider" type="range" multiple value={sliderValue.join(',')} />
      <div className="range">
        <div className="lowDollarAmount" />
        <div className="highDollarAmount" />
      </div>
    </div>
  );
}

export default InputRange;
