import React, { useState } from 'react';
import { Route, Routes, useLocation } from 'react-router-dom';
import { ThemeProvider } from 'styled-components';

import GlobalStyles from 'styles/GlobalStyles';
import LogIn from './views/LogIn/LoginForm';
import BTConnect from './views/BTconnect/Connect';
import Search from './views/BTconnect/Search';
import Profile from './views/BTconnect/Profile';
import WifiSetting from './views/WifiSetting/WifiSetting';

import './App.scss';

const App = () => {
  const [theme, setTheme] = useState(lightTheme);
  const [currentThemeText, setCurrentThemeText] = useState('Light Theme');

  // 각 theme은 state로 관리되며 버튼 클릭 이벤트 시 변경됩니다.
  const switchTheme = () => {
    const nextTheme = theme === lightTheme ? darkTheme : lightTheme;
    setTheme(nextTheme);
    const nextThemeText = theme === lightTheme ? 'Dark Theme' : 'Light Theme';
    setCurrentThemeText(nextThemeText);
  };
  return (
    <ThemeProvider theme={theme}>
      <GlobalStyles />
      <Header switchTheme={switchTheme} />
      <Routes>
        <Route path="/logIn" element={<LogIn />} />
        <Route path="*" element={<LogIn />} />
        <Route path="/Connect" element={<BTConnect />} />
        <Route path="/Search" element={<Search />} />
        <Route path="/Profile" element={<Profile />} />
        <Route path="/WifiSetting" element={<WifiSetting />} />
      </Routes>
    </ThemeProvider>
  );
};

export default App;
