import React, { useState } from 'react';
import styled from 'styled-components';

// 스타일드 컴포넌트를 사용하여 슬라이더 스타일링
const SliderContainer = styled.div`
  width: 100%;
`;

const RangeSlider = styled.input`
  width: 100%;
`;

// ZoomSlider 컴포넌트 정의
const ZoomSlider: React.FC = () => {
  // 줌 레벨 상태 변수
  const [zoomLevel, setZoomLevel] = useState<number>(50); // 초기 줌 레벨을 50으로 설정

  // 슬라이더 값이 변경될 때 호출되는 함수
  const handleSliderChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const newZoomLevel = parseInt(event.target.value);
    setZoomLevel(newZoomLevel);
  };

  return (
    <SliderContainer>
      {/* 레인지 슬라이더를 사용하여 줌 레벨을 조절 */}
      <RangeSlider
        type="range"
        min={0}
        max={100}
        step={1}
        value={zoomLevel}
        onChange={handleSliderChange}
      />
      {/* 현재 줌 레벨 표시 */}
      <p>현재 줌 레벨: {zoomLevel}</p>
    </SliderContainer>
  );
};

export default ZoomSlider;
