import React, { useState } from 'react';
import './RangeSlider.css';

const RangeSlider = () => {
  const [isActive, setIsActive] = useState(false);
  const [values, setValues] = useState([1, 1]); // 초기값 설정

  const handleRangeChange = (event: React.ChangeEvent<HTMLInputElement>, index: number) => {
    const newValue = parseInt(event.target.value);
    const newValues = [...values];
    newValues[index] = newValue;
    setValues(newValues);
  };

  return (
    <div className={`range-slider ${isActive ? 'active' : ''}`}>
      <input
        type="range"
        min={1}
        max={10}
        value={values[0]}
        onChange={(event) => handleRangeChange(event, 0)}
        disabled={!isActive}
      />
      <input
        type="range"
        min={1}
        max={10}
        value={values[1]}
        onChange={(event) => handleRangeChange(event, 1)}
        disabled={!isActive}
      />
      <button type='button' onClick={() => setIsActive(!isActive)}>
        {isActive ? '활성화' : '비활성화'}
      </button>
    </div>
  );
};

export default RangeSlider;
