import React, { useEffect, useState } from 'react';
import './split.scss'; // SCSS 파일 import

function InputRange() {
  const [sliderValue, setSliderValue] = useState<[number, number]>([15, 85]); // sliderValue 상태를 정확한 타입으로 지정
  const [min] = useState<number>(100.23); // min과 max 상태의 타입을 지정
  const [max] = useState<number>(1021.23);

  useEffect(() => {
    const slider = document.querySelector('#slider') as HTMLInputElement; // TypeScript에서 document.querySelector의 반환 타입 지정
    const lowDollarAmount = document.querySelector(
      '.lowDollarAmount',
    ) as HTMLElement; // HTMLElement로 타입 지정
    const highDollarAmount = document.querySelector(
      '.highDollarAmount',
    ) as HTMLElement;

    slider.addEventListener('change', () => {
      const low = min + (max - min) * (sliderValue[0] / 100);
      const high = min + (max - min) * (sliderValue[1] / 100);
      lowDollarAmount.textContent = formatPrice(low);
      highDollarAmount.textContent = formatPrice(high);
    });
  }, [min, max, sliderValue]);

  const formatPrice = (price: number) => {
    if (price !== 0) {
      const formattedPrice =
        '$' +
        parseFloat(price.toFixed(2))
          .toString()
          .replace(/(\d+)(?=(\d{3})+\.?)/g, '$1,');
      return formattedPrice;
    }
    return '';
  };

  return (
    <div className="input-range">
      <input id="slider" type="range" multiple value={sliderValue.join(',')} />
      <div className="range">
        <div className="lowDollarAmount" />
        <div className="highDollarAmount" />
      </div>
    </div>
  );
}

export default InputRange;
