import React, { useState } from 'react';
import { scaleTime } from 'd3-scale';
import { Slider, Rail, Handles, Tracks, Ticks } from 'react-compound-slider';
import {
  format,
  addHours,
  startOfToday,
  endOfToday,
  differenceInMilliseconds,
  addMinutes,
} from 'date-fns';

import SliderRail from './SliderRail';
import Track from './Track';
import Tick from './Tick';
import Handle from './Handle';
import { TimeRangeContainer } from './StyledComponents';
import { UpdateCallbackData } from './types';

interface TimeRangeProps {
  /** Number of steps on the timeline (the default value is 30 minutes) */
  ticksNumber?: number;
  /** Selected interval inside the timeline */
  selectedInterval?: [Date, Date];
  /** Interval to display */
  timelineInterval?: [Date, Date];

  /** ClassName of the wrapping container */
  containerClassName?: string;
  sliderRailClassName?: string;
  /** Number of milliseconds between steps (the default value is 30 minutes) */
  step?: number;
  /** Function that determines the format in which the date will be displayed */
  formatTick?: (ms: number) => string;
  /** Is the selected interval is not valid */
  error?: boolean;
  /** The interaction mode. Value of 1 will allow handles to cross each other.
   * Value of 2 will keep the sliders from crossing and separated by a step.
   * Value of 3 will make the handles pushable and keep them a step apart.
   * ADVANCED: You can also supply a function that will be passed the current
   * values and the incoming update. Your function should return what the state
   * should be set as. */
  mode?: number;
  onChangeCallback?: (formattedNewTime: [Date, Date]) => void;
  onUpdateCallback?: (data: UpdateCallbackData) => void;
  /**
   * Set this variable to true if you want to show a line on the timeline that represents the current time.
   */
  showNow?: boolean;
}

const defaultProps: TimeRangeProps = {
  timelineInterval: [startOfToday(), endOfToday()],
  selectedInterval: [new Date(), addHours(new Date(), 1)],
  containerClassName: '',
  step: 1000 * 60 * 30, // 30 minutes in milliseconds
  ticksNumber: 48, // 30 minutes * 48 = 24 hours
  error: false,
  mode: 3,
  formatTick: (ms: number) => format(new Date(ms), 'HH:mm'),
  showNow: true,
  onChangeCallback: () => 'Change callback not set',
  onUpdateCallback: () => 'Update callback not set',
};

const getTimelineConfig =
  (timelineStart: Date, timelineLength: number) =>
  (date: Date, idPrefix: string) => {
    const percent =
      (differenceInMilliseconds(date, timelineStart) / timelineLength) * 100;
    const value = Number(format(date, 'T'));
    const id = `${idPrefix}-${value}`;
    return { id, percent, value };
  };

const getNowConfig = ([startTime, endTime]: Date[]) => {
  const timelineLength = differenceInMilliseconds(endTime, startTime);
  const getConfig = getTimelineConfig(startTime, timelineLength);
  const source = getConfig(new Date(), 'now-start');
  const target = getConfig(addMinutes(new Date(), 1), 'now-end');
  return { id: 'now-track', source, target };
};

function TimeRange(props: TimeRangeProps) {
  const [nowConfig, setNowConfig] = useState(
    getNowConfig(props.timelineInterval ?? [startOfToday(), endOfToday()]),
  );

  const onChange = (newTime: ReadonlyArray<number>) => {
    const formattedNewTime = newTime.map((t) => new Date(t));
    if (props.onChangeCallback) {
      props.onChangeCallback([formattedNewTime[0], formattedNewTime[1]]);
    }
  };

  const onUpdate = (newTime: ReadonlyArray<number>) => {
    const { onUpdateCallback } = props;

    if (!onUpdateCallback) {
      return;
    }

    const formattedNewTime = newTime.map((t) => new Date(t));
    onUpdateCallback({ error: false, time: formattedNewTime });
  };

  const getDateTicks = () => {
    const { timelineInterval, ticksNumber } = props;
    return scaleTime()
      .domain(timelineInterval ?? [startOfToday(), endOfToday()])
      .ticks(ticksNumber)
      .map((t) => +t);
  };

  return (
    <TimeRangeContainer className={props.containerClassName}>
      <Slider
        step={props.step}
        domain={(props.timelineInterval ?? [startOfToday(), endOfToday()]).map(
          (t) => +t,
        )}
        onUpdate={onUpdate}
        onChange={onChange}
        values={(
          props.selectedInterval ?? [new Date(), addHours(new Date(), 1)]
        ).map((t) => +t)}
        rootStyle={{ position: 'relative', width: '100%' }}
      >
        <Rail>
          {({ getRailProps }) => <SliderRail getRailProps={getRailProps} />}
        </Rail>

        <Handles>
          {({ handles, getHandleProps }) => (
            <>
              {handles.map((handle, index) => (
                <Handle
                  key={handle.id}
                  handle={handle}
                  domain={
                    // 'Date[]'를 'number[]'로 변환
                    (
                      props.timelineInterval ?? [startOfToday(), endOfToday()]
                    ).map((date) => +date)
                  }
                  getHandleProps={
                    // 여기서 마지막 핸들(두 번째 핸들)은 조작을 무시하도록 합니다.
                    index === 1
                      ? () => ({
                          style: {
                            pointerEvents: 'none',
                          },
                        })
                      : getHandleProps
                  }
                />
              ))}
            </>
          )}
        </Handles>

        <Handles>
          {({ handles, getHandleProps }) => (
            <>
              {handles.map((handle, index) => (
                <Handle
                  key={handle.id}
                  handle={handle}
                  domain={
                    // 'Date[]'를 'number[]'로 변환
                    (
                      props.timelineInterval ?? [startOfToday(), endOfToday()]
                    ).map((date) => +date)
                  }
                  getHandleProps={
                    // 여기서 마지막 핸들(두 번째 핸들)은 조작을 무시하도록 합니다.
                    index === 1
                      ? {
                          style: {
                            pointerEvents: 'none',
                          },
                          // 아래의 이벤트 리스너를 추가하여 드래그 이벤트를 무시합니다.
                          onMouseDown: (e) => {
                            e.stopPropagation();
                          },
                        }
                      : getHandleProps
                  }
                />
              ))}
            </>
          )}
        </Handles>

        <Tracks left={false} right={true}>
          {({ tracks, getTrackProps }) => (
            <>
              {tracks?.map(({ id, source, target }) => (
                <Track
                  error={props.error ?? false}
                  key={id}
                  source={source}
                  target={target}
                  getTrackProps={getTrackProps}
                />
              ))}
            </>
          )}
        </Tracks>

        {props.showNow && (
          <Tracks left={false} right={false}>
            {({ getTrackProps }) => (
              <Track
                error={props.error ?? false}
                key={nowConfig?.id}
                source={nowConfig?.source}
                target={nowConfig?.target}
                getTrackProps={getTrackProps}
              />
            )}
          </Tracks>
        )}

        <Ticks values={getDateTicks()}>
          {({ ticks }) => (
            <>
              {ticks.map((tick) => (
                <Tick
                  key={tick.id}
                  tick={tick}
                  count={ticks.length}
                  format={
                    props.formatTick ??
                    ((ms: number) => format(new Date(ms), 'HH:mm'))
                  }
                />
              ))}
            </>
          )}
        </Ticks>
      </Slider>
    </TimeRangeContainer>
  );
}

TimeRange.defaultProps = defaultProps;

export default TimeRange;
