import styled from 'styled-components';

type InputProps = {
  type: string;
  required: boolean;
};

const Form = styled.form`
  max-width: 440px;
  min-width: 90%;
  margin: 0 auto;
  font:
    bold 30px Candara,
    Arial;
  color: #403866;
`;
const Title = styled.h2`
  width: 95%;
  margin-top: 120px;
  font-size: 2.4rem;
  font-weight: bold;
  line-height: 1.2;
  margin-bottom: 30px;
`;
const FormRow = styled.div<{ bottomSpace?: number }>`
  margin-bottom: ${(props) => props.bottomSpace || 20}px;
`;
const Input = styled.input.attrs((props) => ({
  type: props.type || 'text',
  required: true,
}))<InputProps>`
  background: #e6e6e6;
  border: 0;
  font-size: 18px;
  font-weight: bold;
  color: #403866;
  display: block;
  width: 100%;
  height: 62px;
  padding: 0 20px;
  box-sizing: border-box;
`;
const CheckBox = styled.input.attrs((props) => ({
  type: 'checkbox',
}))<{ type: string }>`
  opacity: 0;
  position: absolute;
  ~ label {
    font-size: 16px;
    color: #999;
    display: block;
    position: relative;
    cursor: pointer;
    user-select: none;
    &::before {
      content: '.';
      color: transparent;
      display: inline-flex;
      justify-content: center;
      align-items: center;
      width: 18px;
      height: 18px;
      border-radius: 3px;
      background: #fff;
      border: 2px solid #827ffe;
      margin-right: 16px;
    }
  }
  &:checked ~ label::before {
    content: '\\2713';
    color: #827ffe;
  }
`;
const Button = styled.input`
  background: #827ffe;
  font-size: 18px;
  color: #fff;
  text-transform: uppercase;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 20px;
  width: 100%;
  height: 62px;
  background-color: #827ffe;
  border-radius: 3px;
  border: 0;
  transition: all 0.4s;
  box-sizing: border-box;
  cursor: pointer;
  &:hover {
    background: #403866;
  }
`;

export { Form, Title, FormRow, Input, CheckBox, Button };
