import { set } from 'date-fns';
import { useState } from 'react';

const now = new Date();

const getTodayAtSpecificHour = (hour = 12) =>
  set(now, { hours: hour, minutes: 0, seconds: 0, milliseconds: 0 });

const pointDate = (hour = 12) => set({ hours: hour, minutes: 0 });
export const selectedInterval: [Date] = [getTodayAtSpecificHour(5)];

export const timelineInterval: [Date, Date] = [
  getTodayAtSpecificHour(1),
  getTodayAtSpecificHour(24),
];

export const initialPoints: [Date, Date] = [
  getTodayAtSpecificHour(5),
  getTodayAtSpecificHour(8.5),
];
