import React, { useState } from 'react';
import styled from 'styled-components';

const RangeSliderContainer = styled.div`
  --min: 0;
  --max: 100;
  --step: 5;
  --value: 75;
  --text-value: '75';
  width: clamp(300px, 50vw, 800px);
  min-width: 200px;
  display: inline-block;
  height: max(var(--track-height), var(--thumb-size));
  background: linear-gradient(
      to right,
      var(--ticks-color) var(--ticks-thickness),
      transparent 1px
    )
    repeat-x;
  background-size: var(--tickIntervalPerc) var(--ticks-height);
  background-position-x: calc(
    var(--thumb-size) / 2 - var(--ticks-thickness) / 2
  );
  background-position-y: var(--flip-y, bottom);
  padding-bottom: var(--flip-y, var(--ticks-gap));
  padding-top: calc(var(--flip-y) * var(--ticks-gap));
  position: relative;
  z-index: 1;
`;

const RangeSliderInput = styled.input`
  -webkit-appearance: none;
  width: 100%;
  height: var(--thumb-size);
  margin: 0;
  position: absolute;
  left: 0;
  top: calc(
    50% - max(var(--track-height), var(--thumb-size)) / 2 +
      calc(var(--ticks-gap) / 2 * var(--flip-y, -1))
  );
  cursor: -webkit-grab;
  cursor: grab;
  outline: none;
  background: none;
`;

const RangeSliderOutput = styled.output`
  --y-offset: -5px;
  color: var(--value-active-color);
  box-shadow: 0 0 0 3px var(--value-background);
  pointer-events: none;
  position: absolute;
  z-index: 5;
  background: var(--value-background);
  border-radius: 10px;
  padding: 2px 4px;
  left: calc(((var(--value) - var(--min)) / (var(--max) - var(--min))) * 100%);
  transform: translate(
    calc(var(--x-offset)),
    calc(
      150% * var(--flip) - (var(--y-offset, 0px) + var(--value-offset-y)) *
        var(--flip)
    )
  );
  transition:
    all 0.12s ease-out,
    left 0s;
`;

const RangeSliderProgress = styled.div`
  --start-end: calc(var(--thumb-size) / 2);
  --clip-end: calc(100% - (var(--cb)) * 1%);
  --clip-start: calc(var(--ca) * 1%);
  --clip: inset(-20px var(--clip-end) -20px var(--clip-start));
  position: absolute;
  left: var(--start-end);
  right: var(--start-end);
  top: calc(
    var(--ticks-gap) * var(--flip-y, 0) + var(--thumb-size) / 2 -
      var(--track-height) / 2
  );
  height: calc(var(--track-height));
  background: var(--progress-background, #eee);
  pointer-events: none;
  z-index: -1;
  border-radius: var(--progress-radius);
`;

const RangeSlider = () => {
  const [value, setValue] = useState(75);

  const handleInputChange = (event: any) => {
    const newValue = parseInt(event.target.value);
    setValue(newValue);
    document.documentElement.style.setProperty('--value', newValue);
    document.documentElement.style.setProperty(
      '--text-value',
      JSON.stringify(newValue),
    );
  };

  return (
    <RangeSliderContainer>
      <RangeSliderInput
        type="range"
        min="0"
        max="100"
        step="5"
        value={value}
        onInput={handleInputChange}
      />
      <RangeSliderOutput />
      <RangeSliderProgress />
    </RangeSliderContainer>
  );
};

export default RangeSlider;
