import { set } from 'date-fns';
import { useState } from 'react';

const now = new Date();

const getTodayAtSpecificHour = (hour = 12) =>
  set(now, { hours: hour, minutes: 0, seconds: 0, milliseconds: 0 });

export const splitTimes: [Date, Date, Date] = [
  getTodayAtSpecificHour(2),
  getTodayAtSpecificHour(8),
  getTodayAtSpecificHour(19),
];

export const selectedInterval: [Date] = [splitTimes[0]];

export const timelineInterval: [Date, Date] = [
  getTodayAtSpecificHour(0),
  getTodayAtSpecificHour(24),
];
