import React, { useEffect, useState } from 'react';
import { EncryptStorage } from 'encrypt-storage';
import styled from 'styled-components';
import { Form, Title, FormRow, CheckBox, Button } from './LoginFormStyle';

interface Credentials {
  user: string;
  password: string;
}

type InputProps = {
  type: string;
  required: boolean;
};

const Input = styled.input.attrs((props) => ({
  type: props.type || 'text',
  required: true,
}))<InputProps>`
  background: #e6e6e6;
  border: 0;
  font-size: 18px;
  font-weight: bold;
  color: #403866;
  display: block;
  width: 100%;
  height: 62px;
  padding: 0 20px;
  box-sizing: border-box;
`;

const LoginForm = () => {
  const encryptStorage = new EncryptStorage('SECRET_KEY');
  const [rememberMe, setRememberMe] = useState(false);
  const [formData, setFormData] = useState<Credentials>({
    user: '',
    password: '',
  });

  const handleChange = (ev: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [ev.target.name]: ev.target.value,
    });
  };
  const handleCheck = () => {
    if (rememberMe) {
      encryptStorage.removeItem('loginCredentials');
    }
    setRememberMe((rememberMe) => !rememberMe);
  };
  const handleSubmit = (ev: React.FormEvent<HTMLFormElement>) => {
    if (rememberMe) {
      encryptStorage.setItem('loginCredentials', JSON.stringify(formData));
    }
  };

  useEffect(() => {
    const storedData = encryptStorage.getItem('loginCredentials');
    if (storedData) {
      setRememberMe(true);
      setFormData(storedData);
    }
  }, []);

  return (
    <>
      <Title>
        네오스마트펜
        <br />
        클라우드 설정
      </Title>
      <Form onSubmit={handleSubmit} action="">
        <FormRow>
          <Input
            placeholder="Username"
            onChange={handleChange}
            name="user"
            value={formData.user}
          />
        </FormRow>
        <FormRow>
          <Input
            type="password"
            placeholder="Password"
            onChange={handleChange}
            name="password"
            value={formData.password}
          />
        </FormRow>
        <FormRow bottomSpace={40}>
          <CheckBox
            id="rememberMe"
            type="checkbox"
            onChange={handleCheck}
            checked={rememberMe}
          />
          {/* <label htmlFor='rememberMe'>Remember me</label> */}
        </FormRow>
        <Button type="submit" value="로그인" />
      </Form>
    </>
  );
};
export default LoginForm;
