import React from 'react';
import { Route, Routes } from 'react-router-dom';
/* import * as ROUTES from './routes'; */

import LogIn from './views/LogIn/LoginForm';

import './App.css';

const App = () => {
  // const [theme, settheme] = useState(neolabTheme.theme0_kr);
  const isMobileDevice = true;
  const location = useLocation();
  console.log(location);

  const { userStore, pageStore, noteStore, strokeStore, audioStore } =
    useStores();
  const extra = userStore.userExtraData;
  const [theme, setTheme] = useState(extra.theme); // 테마

  const connectLiveRoom = async () => {
    const ndp = NDP.instance;
    const { relay } = ndp;

    const rooms = await relay.getRoom();

    if (rooms) {
      const existed = rooms?.resultElements?.filter(
        (e) => e.ownerId === userStore.userData.id,
      );

      if (existed.length) {
        relay.setRoom(existed[0]);
      } else {
        const room = await relay.createRoom();
        if (room.entranceUrl) {
          relay.setRoom(room);
        } else {
          console.error('Create Room Failed. Please check the server status.');
          return;
        }
      }
      await relay.connectRoom();

      await relay.relayWS.join();

      NDP.instance.relay.relayWS.on('UPLOAD_END', listenUploadEnd);
    }
  };

  useEffect(() => {
    NDPSyncController.getInstance();
    PenManager.getInstance();
    DatabaseController.getInstance();

    // const ti = getThumbManager(); //여기서 init 해주지 않으면 ready가 pending이어서 진행되지 않음
  }, []);

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [location]);

  useEffect(() => {
    async function getUserStrokes() {
      // const asyncIterableStroke = NDP.instance.inkStore.getStrokeByStrokeIter(0);
      // for await (const pages of asyncIterableStroke) {
      //   NDPSyncController.instance.updateInkStoreage(pages);
      // }
    }

    async function getNoteList() {
      await noteStore.loadLocalNoteList();
      await pageStore.loadLocalPages();
      strokeStore.setStrokeLoading(LoadingStateEnum.SUCCESS);
    }

    if (userStore.userData) {
      getNoteList();
      getUserStrokes();
      connectLiveRoom();
      audioStore.getRecordedPageList();
    }

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userStore.userData]);

  // 사용자 테마 세팅
  useEffect(() => {
    if (userStore.userExtraData) {
      const { theme } = userStore.userExtraData;
      setTheme(theme);
    }
  }, [userStore.userExtraData]);

  const listenUploadEnd = async (res) => {
    const lastUploadTime = await getInkManager().getLastSyncUploadTime();
    const pages = (await NDP.instance.inkStore.getStrokeByUpload(
      lastUploadTime,
      res.createdDate + 1000, // 업로드되는데 시간이 걸려서 1000ms 더해줘야함
    )) as NDPInkStorePage[];
    const ln = pages?.length ?? 0;

    if (ln) {
      /**
       * dummy 페이지이고, digital이 아닌 경우에만 해당
       *
       * 1. noteUUID가 PageStore와 NoteStore 있는경우
       *    - pageUUID만 바꿔주면 된다
       * 2. noteUUID도 새로 받은 상황
       *    - noteStore에 가서 noteId도 바꿔주고, pageStore에서 noteId, pageId 바꿔줘야 함
       */

      let needToBeReplaced = false;
      let dummyNoteId = '';
      let dummyPageId = '';
      let dummySobp: any = {};

      // eslint-disable-next-line no-restricted-syntax
      for (const page of pages) {
        const targetNote = noteStore.noteList.find(
          (e) => e.id === page.noteUUID,
        );

        if (targetNote) {
          const targetPage = pageStore.allPageList[targetNote.id].find(
            (e) => e.id === page.pageUUID,
          );

          if (targetPage) {
            //페이지도 있다? 그럼 할 거 없음
            break;
          } else {
            //노트는 있는데 페이지는 없는 경우
            //더미로 들어가있다는 뜻. PageStore에서 sobp랑 dummy로 찾아
            const dummyPage = pageStore.allPageList[targetNote.id].find((e) => {
              if (
                e.section === page.section &&
                e.owner === page.owner &&
                e.bookCode === page.bookCode &&
                e.dummy
              ) {
                return true;
              }
              return false;
            });

            if (dummyPage !== undefined) {
              dummyPageId = dummyPage.id;
              dummyPage.dummy = false;
              dummyPage.id = page.pageUUID;
              dummyPage.noteId = page.noteUUID;
            }

            needToBeReplaced = true;

            dummySobp = {
              section: dummyPage.section,
              owner: dummyPage.owner,
              book: dummyPage.bookCode,
              page: dummyPage.pageNumber,
              pageUuid: dummyPageId,
              noteUuid: targetNote.id,
            };
          }
        } else {
          //노트도 처음 생성하는 경우. 현재는 더미 노트로 존재
          const dummyNote = noteStore.noteList.find((e) => {
            if (
              e.section === page.section &&
              e.owner === page.owner &&
              e.bookCode === page.bookCode &&
              e.dummy
            ) {
              return true;
            }
            return false;
          });

          if (dummyNote !== undefined) {
            dummyNoteId = dummyNote.id;
            dummyNote.dummy = false;

            pageStore.allPageList[page.noteUUID] = [];
            pageStore.allPageList[page.noteUUID] =
              pageStore.allPageList[dummyNote.id];

            delete pageStore.allPageList[dummyNote.id];

            dummyNote.id = page.noteUUID;
          }

          const dummyPage = pageStore.allPageList[page.noteUUID].find((e) => {
            if (
              e.section === page.section &&
              e.owner === page.owner &&
              e.bookCode === page.bookCode &&
              e.dummy
            ) {
              return true;
            }
            return false;
          });

          if (dummyPage !== undefined) {
            dummyPageId = dummyPage.id;
            dummyPage.dummy = false;
            dummyPage.id = page.pageUUID;
            dummyPage.noteId = page.noteUUID;
          }

          needToBeReplaced = true;
          dummySobp = {
            section: dummyPage.section,
            owner: dummyPage.owner,
            book: dummyPage.bookCode,
            page: dummyPage.pageNumber,
            pageUuid: dummyPageId,
            noteUuid: dummyNoteId,
          };
        }

        const dummySobpId = makeNPageIdStrWithUuid(dummySobp);
        const strokesOnDummyPage =
          CachedInkStorage.instance.completedOnPage.get(dummySobpId);
        if (strokesOnDummyPage.arr.array) {
          for (const stroke of strokesOnDummyPage.arr.array) {
            stroke.pageUuid = page.pageUUID;
            stroke.noteUuid = page.noteUUID;
          }

          const realSobpId = makeNPageIdStrWithUuid({
            section: page.section,
            owner: page.owner,
            book: page.bookCode,
            page: page.pageNumber,
            pageUuid: page.pageUUID,
            noteUuid: page.noteUUID,
          });

          CachedInkStorage.instance.completedOnPage.set(
            realSobpId,
            strokesOnDummyPage,
          );
          CachedInkStorage.instance.completedOnPage.delete(dummySobpId);
        }
      }

      const thumbManager = getThumbManager();
      for (let i = 0; i < ln; i++) {
        const pr = thumbManager.invalidateIfNeeded({
          noteId: pages[i].noteUUID,
          pageId: pages[i].pageUUID,
          strokeLastUploadTime: res.createdDate,
          doNotNotify: false,
          force: true,
          needToBeReplaced,
        });
        await pr;
      }
    }
  };

  const saveStroke = (event) => {
    // const { stroke } = event;
    // stroke.pageUuid = pageStore.currentPage.id;
    // stroke.noteUuid = pageStore.currentPage.noteId;
    // DatabaseController.instance.saveStroke(stroke);
  };

  const handleCompletedDot = (event) => {
    const { stroke } = event;
    stroke.pageUuid = pageStore.currentPage.id;
    stroke.noteUuid = pageStore.currentPage.noteId;

    //live는 dot로 보내니까 여기서 중복으로 보내면 안됨
    if (!event.isLive) {
      NDPSyncController.instance.handleCompletedDot(event);
    }
  };

  useEffect(() => {});
  return (
    // 임시조치
    <ThemeProvider theme={theme === 0 ? lightTheme : lightTheme}>
      <GlobalStyles />
      <Notifications />
      <PenBLEConnectDrawer />
      <Recorder />
      {isMobileDevice ? (
        <Routes location={location}>
          {/* Public route */}
          <Route path="/signIn" element={<MobileSignIn />} />
          <Route
            path={NDP_LOGIN_CALLBACK_PATH}
            element={<UserAuthReactElement />}
          />
          <Route path="*" element={<MobileSignIn />} />

          {/* Private route */}
          <Route element={<PrivateRoute />}>
            <Route element={<MobileDefaultLayout />}>
              <Route path="/" element={<MobileTimeLine />} />
              <Route path={ROUTES.MAIN_TIMELINE} element={<MobileTimeLine />} />
              <Route
                path={ROUTES.MAIN_NOTE_LIST}
                element={<MobileNoteList />}
              />
              <Route path={ROUTES.MAIN_SETTING} element={<MobileSetting />} />
            </Route>
            <Route path={ROUTES.MAIN_PAGE_LIST} element={<MobilePageList />} />
            <Route path={ROUTES.MAIN_TAG} element={<MobileTag />} />
            <Route path={ROUTES.MAIN_TAG_PAGE} element={<MobileTagPages />} />
            <Route path={ROUTES.MAIN_SEARCH} element={<MobileSearch />} />
            <Route path={ROUTES.MAIN_RECORD} element={<MobileRecord />} />
            <Route path={ROUTES.MAIN_FAVORITE} element={<MobileFavorite />} />
            <Route
              path={ROUTES.MAIN_THEME}
              element={<MobileTheme setTheme={setTheme} />}
            />
            <Route path={ROUTES.MAIN_PROFILE} element={<MobileProfile />} />
            <Route
              path={ROUTES.MAIN_PEN_ROOT}
              element={<MobilePenInfoRouter />}
            />
            <Route
              path={ROUTES.MAIN_SHARE_EMAIL}
              element={<SettingShareEmail />}
            />
            <Route
              path={ROUTES.MAIN_INTER_CAL}
              element={<SettingInterCalender />}
            />
            <Route
              path={ROUTES.MAIN_PAGE_DETAIL_ROOT}
              element={<MobilePageDetailRouter />}
            />
            <Route
              path={ROUTES.MAIN_SELECT_NOTE}
              element={<MobileDefaultSelectNote />}
            />
            <Route
              path={ROUTES.MAIN_WITHDRAWAL}
              element={<MobileWithdrawal />}
            />
            <Route path={ROUTES.MAIN_REPLAY} element={<MobileReplayView />} />
            <Route path={ROUTES.MAIN_HWR_LANG} element={<SettingHwrLang />} />
            <Route
              path={ROUTES.MAIN_APP_VERSION}
              element={<SettingAppVersion />}
            />
            <Route path={ROUTES.MAIN_PEN_PASSWORD} element={<PenPassword />} />
            <Route
              path={ROUTES.MAIN_PEN_FIRMWARE_VERSION}
              element={<PenFirmwareVersion />}
            />
            <Route
              path={ROUTES.MAIN_PEN_OFFLINE_DATA}
              element={<PenOfflineData />}
            />
          </Route>
        </Routes>
      ) : (
        <Routes>
          {/* Public route */}
          <Route path="/signIn" element={<SignIn />} />
          <Route
            path={NDP_LOGIN_CALLBACK_PATH}
            element={<UserAuthReactElement />}
          />
          <Route path="*" element={<SignIn />} />

          {/* Private route */}
          <Route element={<PrivateRoute />}>
            <Route element={<DefaultLayout />}>
              <Route path="/" element={<Home />} />
              <Route path={ROUTES.MAIN_TIMELINE} element={<TimeLine />} />
              <Route path={ROUTES.MAIN_NOTE_LIST} element={<NoteList />} />
              <Route path={ROUTES.MAIN_PAGE_LIST} element={<PageList />} />
              <Route path={ROUTES.MAIN_TAG} element={<Tag />} />
              <Route path={ROUTES.MAIN_FAVORITE} element={<Favorite />} />
              <Route path={ROUTES.MAIN_RECORD} element={<Record />} />
              <Route path={ROUTES.MAIN_SETTING} element={<Setting />} />
            </Route>
            <Route path={ROUTES.MAIN_PAGE_DETAIL} element={<PageDetail />} />
          </Route>
        </Routes>
      )}
    </ThemeProvider>
  );
};

export default App;
