import React, { useEffect, useState } from 'react';
import { EncryptStorage } from 'encrypt-storage';
import {
  Form,
  Title,
  FormRow,
  Input,
  CheckBox,
  Button,
} from './LoginFormStyle';

interface Credentials {
  user: string;
  password: string;
}

const LoginForm = () => {
  const encryptStorage = new EncryptStorage('SECRET_KEY');
  const [rememberMe, setRememberMe] = useState(false);
  const [formData, setFormData] = useState<Credentials>({
    user: '',
    password: '',
  });

  const handleCheck = () => {
    if (rememberMe) {
      encryptStorage.removeItem('loginCredentials');
    }
    setRememberMe((rememberMe) => !rememberMe);
  };
  const handleSubmit = (ev: React.FormEvent<HTMLFormElement>) => {
    if (rememberMe) {
      encryptStorage.setItem('loginCredentials', JSON.stringify(formData));
    }
  };

  useEffect(() => {
    const storedData = encryptStorage.getItem('loginCredentials');
    if (storedData) {
      setRememberMe(true);
      setFormData(storedData);
    }
  }, []);

  return (
    <>
      <Title>Login</Title>
      <Form onSubmit={handleSubmit} action="">
        <FormRow>
          {/*           <Input
            placeholder="Username"
            onChange={handleChange}
            name="user"
            value={formData.user}
          /> */}
        </FormRow>
        <FormRow>
          {/*           <Input
            type="password"
            placeholder="Password"
            onChange={handleChange}
            name="password"
            value={formData.password}
          /> */}
        </FormRow>
        <FormRow bottomSpace={40}>
          <CheckBox
            id="rememberMe"
            type="checkbox"
            onChange={handleCheck}
            checked={rememberMe}
          />
          {/* <label htmlFor='rememberMe'>Remember me</label> */}
        </FormRow>
        <Button type="submit" value="Login" />
      </Form>
    </>
  );
};
export default LoginForm;
