import styled from 'styled-components';

export const Container = styled.div`
  position: relative;
  height: 20vh;
  padding: 24px;
  background: #fff;
`;

export const Slider = styled.div`
  position: relative;
  width: 100%;
`;

export const Track = styled.div`
  position: absolute;
  border-radius: 3px;
  height: 5px;
  background-color: #ced4da;
  width: 100%;
  z-index: 1;
`;

export const Range = styled.div`
  position: absolute;
  border-radius: 3px;
  height: 5px;
  background-color: #9fe5e1;
  z-index: 2;
`;

export const LeftValue = styled.div`
  position: absolute;
`;

export const RightValue = styled.div`
  position: absolute;
`;

export const Thumb = styled.input.attrs({ type: 'range' })`
  -webkit-appearance: none;
  pointer-events: none;
  display: inherit;
  height: 0;
  outline: none;
  width: 100%;

  &::-webkit-slider-thumb {
    height: 18px;
    width: 18px;
    border-radius: 50%;
    background-color: #fff;
    box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.2);
    cursor: pointer;
    margin-top: 4px;
    pointer-events: all;
    position: relative;
    top: 13px;
    -webkit-appearance: none;
    z-index: 10;
  }

  &::-moz-range-thumb {
    height: 18px;
    width: 18px;
    border-radius: 50%;
    background-color: #fff;
    box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.2);
    cursor: pointer;
    margin-top: 4px;
    pointer-events: all;
    position: relative;
    top: 13px;
    -webkit-appearance: none;
  }
`;

export const ThumbLeft = styled(Thumb)`
  z-index: 3;
  left: 5px;
`;

export const ThumbRight = styled(Thumb)`
  z-index: 4;
  right: 10px;
`;

export const Point = styled.div`
  width: 10px;
  height: 10px;
  background-color: rgba(178, 215, 218, 0.3); // 원하는 색상으로 변경
  border-radius: 50%;
  cursor: pointer;
  position: absolute;
  top: 10px;
  z-index: 5;
  transition: transform 0.2s;
  transform: translateX(-5px);
`;
