import React, { useState, useEffect } from 'react';
import * as s from './splitStyle';

const Split = () => {
  const fixedMinTime = 0; // 이 값을 실제 값으로 초기화해야 합니다.
  const fixedMaxTime = 100; // 이 값을 실제 값으로 초기화해야 합니다.
  const TimeGap = 10; // 이 값을 실제 값으로 초기화해야 합니다.

  const [rangeMinValue, setRangeMinValue] = useState(fixedMinTime);
  const [rangeMaxValue, setRangeMaxValue] = useState(fixedMaxTime);
  const [rangeMinPercent, setRangeMinPercent] = useState(0);
  const [rangeMaxPercent, setRangeMaxPercent] = useState(0);

  const prcieRangeMinValueHandler = (e) => {
    setRangeMinValue(parseInt(e.target.value));
  };

  const prcieRangeMaxValueHandler = (e) => {
    setRangeMaxValue(parseInt(e.target.value));
  };

  const twoRangeHandler = () => {
    if (rangeMaxValue - rangeMinValue < TimeGap) {
      setRangeMaxValue((rangeMinValue) => rangeMinValue + TimeGap);
      setRangeMinValue((rangeMaxValue) => rangeMaxValue - TimeGap);
    } else {
      setRangeMinPercent(() => (rangeMinValue / fixedMaxTime) * 100);
      setRangeMaxPercent(() => 100 - (rangeMaxValue / fixedMaxTime) * 100);
    }
  };

  useEffect(() => {
    twoRangeHandler();
  }, [rangeMinValue, rangeMaxValue]);

  return (
    <div style={{ padding: '50px' }}>
      <s.FilterTimeSlide>
        <s.FilterTimeSlideInner
          rangeMinPercent={rangeMinPercent}
          rangeMaxPercent={rangeMaxPercent}
        />
      </s.FilterTimeSlide>
      <s.FilterTimeRangeWrap>
        <s.FilterTimeRangeMin
          type="range"
          min={fixedMinTime}
          max={fixedMaxTime - TimeGap}
          step="1000"
          value={rangeMinValue}
          onChange={(e) => {
            prcieRangeMinValueHandler(e);
            twoRangeHandler();
          }}
        />
        <s.FilterTimeRangeMax
          type="range"
          min={fixedMinTime + TimeGap}
          max={fixedMaxTime}
          step="1000"
          value={rangeMaxValue}
          onChange={(e) => {
            prcieRangeMaxValueHandler(e);
            twoRangeHandler();
          }}
        />
      </s.FilterTimeRangeWrap>
    </div>
  );
};

export default Split;
