import { set } from 'date-fns';

const now = new Date();

const getTodayAtSpecificHour = (hour = 12) =>
  set(now, { hours: hour, minutes: 0, seconds: 0, milliseconds: 0 });

/* export const selectedInterval = [
  getTodayAtSpecificHour(),
  getTodayAtSpecificHour(24),
];

export const timelineInterval = [
  getTodayAtSpecificHour(7),
  getTodayAtSpecificHour(24),
]; */
// datesSource.js 파일에서 selectedInterval과 timelineInterval을 다음과 같이 수정합니다.
export const selectedInterval: [Date, Date] = [new Date(), new Date()];
export const timelineInterval: [Date, Date] = [new Date(), new Date()];
