import styled from 'styled-components';

export const Container = styled.div`
  position: relative;
  height: 20vh;
  padding: 24px;
  background: #fff;
`;

export const Slider = styled.div`
  position: relative;
  width: 100%;
`;

export const Track = styled.div`
  position: absolute;
  border-radius: 100px;
  height: 30px;
  background-color: #f7f7f7;
  width: 100%;
  z-index: 1;
  &:after {
    content: '';
    position: absolute;
    left: -10px;
    background: #f7f7f7;
    width: 50px;
    height: 30px;
    border-radius: 100px;
    z-index: 1;
  }
  &:before {
    content: '';
    position: absolute;
    right: -10px;
    background: #f7f7f7;
    width: 50px;
    height: 30px;
    border-radius: 100px;
    z-index: 1;
  }
`;

export const Range = styled.div`
  position: absolute;
  border-radius: 100px;
  height: 30px;
  background-color: #00838f;
  z-index: 2;
  &:after {
    content: '';
    position: absolute;
    left: -10px;
    background: #00838f;
    width: 50px;
    height: 30px;
    border-radius: 100px;
    z-index: 1;
  }
  &:before {
    content: '';
    position: absolute;
    right: -10px;
    background: #00838f;
    width: 50px;
    height: 30px;
    border-radius: 100px;
    z-index: 1;
  }
`;

export const LeftValue = styled.div`
  position: absolute;
  color: #333;
  font-size: 12px;
  margin-top: 50px;
  left: 6px;
`;

export const RightValue = styled.div`
  position: absolute;
  color: #333;
  font-size: 12px;
  margin-top: 50px;
  right: -4px;
`;

export const Thumb = styled.input.attrs({ type: 'range' })`
  -webkit-appearance: none;
  pointer-events: none;
  display: inherit;
  height: 0;
  outline: none;
  width: 100%;
`;

export const ThumbLeft = styled(Thumb)`
  z-index: 3;
  left: 5px;
`;

export const ThumbRight = styled(Thumb)`
  z-index: 4;
  right: 10px;
`;

export const Point = styled.div`
  width: 10px;
  height: 10px;
  background-color: rgba(178, 215, 218, 0.3); // 원하는 색상으로 변경
  border-radius: 50%;
  cursor: pointer;
  position: absolute;
  top: 10px;
  z-index: 5;
  transition: transform 0.2s;
  transform: translateX(-5px);
`;
