import React, { useState } from 'react';
import * as s from './splitStyle';

interface SplitProps {
  fixedMinTime: number;
  fixedMaxTime: number;
  TimeGap: number;
}

const Split: React.FC<SplitProps> = ({
  fixedMinTime,
  fixedMaxTime,
  TimeGap,
}) => {
  const [rangeMinValue, setRangeMinValue] = useState<number>(fixedMinTime);
  const [rangeMaxValue, setRangeMaxValue] = useState<number>(fixedMaxTime);
  const [rangeMinPercent, setRangeMinPercent] = useState<number>(0);
  const [rangeMaxPercent, setRangeMaxPercent] = useState<number>(0);

  const TimeRangeMinValueHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
    setRangeMinValue(parseInt(e.target.value, 10)); // 진수 파라미터를 10으로 설정
  };

  const TimeRangeMaxValueHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
    setRangeMaxValue(parseInt(e.target.value, 10)); // 진수 파라미터를 10으로 설정
  };

  const twoRangeHandler = () => {
    if (rangeMaxValue - rangeMinValue < TimeGap) {
      setRangeMaxValue(rangeMinValue + TimeGap);
      setRangeMinValue(rangeMaxValue - TimeGap);
    } else {
      setRangeMinPercent((rangeMinValue / fixedMaxTime) * 100);
      setRangeMaxPercent(100 - (rangeMaxValue / fixedMaxTime) * 100);
    }
  };

  return (
    <div style={{ padding: '50px' }}>
      <s.FilterTimeSlide>
        <s.FilterTimeSlideInner
          rangeMinPercent={rangeMinPercent}
          rangeMaxPercent={rangeMaxPercent}
        />
      </s.FilterTimeSlide>
      <s.FilterTimeRangeWrap>
        <s.FilterTimeRangeMin
          type="range"
          min={fixedMinTime}
          max={fixedMaxTime - TimeGap}
          step={1000}
          value={rangeMinValue}
          onChange={(e) => {
            TimeRangeMinValueHandler(e);
            twoRangeHandler();
          }}
        />
        <s.FilterTimeRangeMax
          type="range"
          min={fixedMinTime + TimeGap}
          max={fixedMaxTime}
          step={1000}
          value={rangeMaxValue}
          onChange={(e) => {
            TimeRangeMaxValueHandler(e);
            twoRangeHandler();
          }}
        />
      </s.FilterTimeRangeWrap>
    </div>
  );
};

export default Split;
