import React, {
  FC,
  useCallback,
  useEffect,
  useState,
  useRef,
  MouseEvent,
} from 'react';

import * as s from './multiRangeSliderStyle';

interface MultiRangeSliderProps {
  min: number;
  max: number;
  points: number[];
  onPointClick: (value: number) => void;
}

const MultiRangeSlider: FC<MultiRangeSliderProps> = ({
  min,
  max,
  points,
  onPointClick,
}) => {
  const [minVal, setMinVal] = useState(min);
  const [maxVal, setMaxVal] = useState(max);
  const [isDraggingMin, setIsDraggingMin] = useState(false);
  const [isDraggingMax, setIsDraggingMax] = useState(false);
  const minValRef = useRef(min);
  const maxValRef = useRef(max);
  const range = useRef<HTMLDivElement>(null);

  const getPercent = useCallback(
    (value: number) => Math.round(((value - min) / (max - min)) * 100),
    [min, max],
  );

  useEffect(() => {
    const minPercent = getPercent(minVal);
    const maxPercent = getPercent(maxValRef.current);

    if (range.current) {
      range.current.style.left = `${minPercent}%`;
      range.current.style.width = `${maxPercent - minPercent}%`;
    }
  }, [minVal, getPercent]);

  useEffect(() => {
    const minPercent = getPercent(minValRef.current);
    const maxPercent = getPercent(maxVal);

    if (range.current) {
      range.current.style.width = `${maxPercent - minPercent}%`;
    }
  }, [maxVal, getPercent]);

  const handlePointClick = (value: number) => {
    if (value > minVal && value < maxVal) {
      onPointClick(value);
      setMinVal(value);
      minValRef.current = value;
    }
  };

  const handleThumbLeftMouseDown = () => {
    setIsDraggingMin(true);
  };

  const handleThumbRightMouseDown = () => {
    setIsDraggingMax(true);
  };

  const handleMouseMove = (event: MouseEvent) => {
    if (isDraggingMin) {
      const newValue = Math.min(
        maxVal - 1,
        Math.max(min, valueFromMouseEvent(event)),
      );
      setMinVal(newValue);
      minValRef.current = newValue;
    } else if (isDraggingMax) {
      const newValue = Math.max(
        minVal + 1,
        Math.min(max, valueFromMouseEvent(event)),
      );
      setMaxVal(newValue);
      maxValRef.current = newValue;
    }
  };

  const handleMouseUp = () => {
    setIsDraggingMin(false);
    setIsDraggingMax(false);
  };

  const valueFromMouseEvent = (event: MouseEvent) => {
    if (range.current) {
      const rangeRect = range.current.getBoundingClientRect();
      const offsetX = event.clientX - rangeRect.left;
      return (offsetX / rangeRect.width) * (max - min) + min;
    }
    return 0;
  };

  useEffect(() => {
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, []);

  return (
    <s.Container>
      <s.Slider>
        <s.Track />
        <s.Range ref={range} />
        {points.map((point) => (
          <s.Point
            key={point}
            onClick={() => handlePointClick(point)}
            style={{ left: `${getPercent(point)}%` }}
          />
        ))}
        <s.LeftThumb
          style={{ left: `${getPercent(minVal)}%` }}
          onMouseDown={handleThumbLeftMouseDown}
        />
        <s.RightThumb
          style={{ left: `${getPercent(maxVal)}%` }}
          onMouseDown={handleThumbRightMouseDown}
        />
      </s.Slider>
      <s.LeftValue>{minVal}</s.LeftValue>
      <s.RightValue>{maxVal}</s.RightValue>
    </s.Container>
  );
};

export default MultiRangeSlider;
