export const lightTheme = {
  BasebgColor: '#f5f5f5',
  PopupbgColor: '#ffffff',
  Textf1Color: '#1e1e1e',
  Textf2Color: '#424242',
  Textf3Color: '#757575',
  Textf4Color: '#a2a2a2',
  Textf5Color: '#e9e9e9',
  Linel1Color: '#212121',
  Linel2Color: '#bababa',
  Linel3Color: '#cfcfcf',
  Linel4Color: '#dcdcdc',
  Iconi1Color: '#1e1e1e',
  Iconi2Color: '#b8b8b8',
  Iconi3Color: '#e9e9e9',
  BtngrayColor: '#e3e3e3',
  Btngray2Color: '#bfbfbf',
  Btngray3Color: '#ffffff',
  Btngray4Color: '#f1f1f1',
};

export const darkTheme = {
  BasebgColor: '#1e1e1e',
  PopupbgColor: '#303030',
  Textf1Color: '#f5f5f5',
  Textf2Color: '#dddddd',
  Textf3Color: '#cecece',
  Textf4Color: '#8d8d93',
  Textf5Color: '#333333',
  Linel1Color: '#333333',
  Linel2Color: '#404043',
  Linel3Color: '#464649',
  Linel4Color: '#434346',
  Iconi1Color: '#f5f5f5',
  Iconi2Color: '#6e6e73',
  Iconi3Color: '#333333',
  BtngrayColor: '#1c1c1e',
  Btngray2Color: '#424242',
  Btngray3Color: '#424242',
  Btngray4Color: '#333333',
};

export const theme = {
  lightTheme,
  darkTheme,
};

export const mixins = {
  // flex
  flexBox: (direction = 'row', align = 'center', justify = 'center') => `
    display: flex;
    flex-direction: ${direction};
    align-items: ${align};
    justify-content: ${justify};
  `,
};

export default theme;
