import styled from 'styled-components';

export const Container = styled.div`
  height: 20vh;
  padding: 24px;
  background: #fff;
`;

export const Slider = styled.div`
  position: relative;
  width: 100%;
`;

export const Track = styled.div`
  position: absolute;
  border-radius: 100px;
  height: 30px;
  background-color: #f7f7f7;
  width: 100%;
  z-index: 1;
`;

export const Range = styled.div`
  position: absolute;
  border-radius: 100px;
  height: 30px;
  background-color: #00838f;
  z-index: 2;
`;

export const LeftValue = styled.div`
  position: absolute;
  color: #333;
  font-size: 12px;
  margin-top: 20px;
  left: 6px;
`;

export const RightValue = styled.div`
  position: absolute;
  color: #333;
  font-size: 12px;
  margin-top: 20px;
  right: -4px;
`;

export const Thumb = styled.input.attrs({ type: 'range' })`
  -webkit-appearance: none;
  pointer-events: none;
  position: absolute;
  height: 0;
  width: calc(100% - 24px);
  outline: none;

  &::-webkit-slider-thumb {
    height: 18px;
    width: 18px;
    border-radius: 50%;
    background-color: #fff;
    box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.2);
    cursor: pointer;
    margin-top: 4px;
    pointer-events: all;
    position: relative;
    top: 13px;
    -webkit-appearance: none;
  }

  &::-moz-range-thumb {
    height: 30px;
    width: 30px;
    border-radius: 50%;
    background-color: #fff;
    box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.2);
    cursor: pointer;
    margin-top: 4px;
    pointer-events: all;
    position: relative;
    -webkit-appearance: none;
  }
`;

export const ThumbLeft = styled(Thumb)`
  z-index: 3;
`;

export const ThumbRight = styled(Thumb)`
  z-index: 4;
`;
