/* eslint-disable react/jsx-props-no-spreading */
import React from 'react';
import { NavLink } from 'react-router-dom';
import classNames from 'classnames/bind';

import { ReactComponent as Home } from '../../assets/icon/Home.svg';
import { ReactComponent as Search } from '../../assets/icon/nav_Search.svg';
import { ReactComponent as Profile } from '../../assets/icon/nav_Profile.svg';
import styles from '../../styles/App.module.scss';
import { styled } from '@mui/material/styles';

import * as ROUTES from '../../routes';

interface ILayoutBottom {
  isShow: boolean;
}

const Container = styled('div')({
  position: 'fixed',
  width: '100vw',
  maxWidth: '500px',
  bottom: '-1px',
  transitionDuration: '400ms',
  '&.leaveContainer': {
    transform: 'translateY(+48px)',
  },
  zIndex: 4,
});

const cx = classNames.bind(styles);

function MobileDefaultLayoutBottom({ isShow }: ILayoutBottom) {
  const [leaveContainer, setLeaveContainer] = React.useState(false);

  React.useEffect(() => {
    if (isShow) {
      setLeaveContainer(false);
    } else {
      setLeaveContainer(true);
    }
  }, [isShow]); // eslint-disable-line react-hooks/exhaustive-deps

  return (
    <Container className={`${leaveContainer ? 'leaveContainer' : ''}`}>
      <div className={cx('BottomSection', 'MainBottombar', 'mbtmbarcst')}>
        <ul className={styles.BottomNavList}>
          <li className={styles.navTimeline}>
            <NavLink
              to={ROUTES.MAIN_CONNECT}
              className={({ isActive }) => (isActive ? 'active' : 'inactive')}
            >
              <div className={styles.navIcon}>
                <div className={styles.wrapArea}>
                  <Home />
                </div>
              </div>
            </NavLink>
          </li>
          <li className={styles.navNotebook}>
            <div className={styles.navIcon}>
              <div className={styles.wrapArea}>
                <Search />
              </div>
            </div>
          </li>
          {/* 미니앱 */}
          <li className={styles.navMiniapp}>
            <div className={styles.navIcon}>
              <div className={styles.wrapArea}>
                <Profile />
              </div>
            </div>
          </li>
        </ul>
      </div>
    </Container>
  );
}

export default MobileDefaultLayoutBottom;
