import React, {
  ChangeEvent,
  FC,
  useCallback,
  useEffect,
  useState,
  useRef,
  MouseEvent,
} from 'react';

import * as s from './multiRangeSliderStyle';

interface MultiRangeSliderProps {
  min: number;
  max: number;
  points: number[];
  onPointClick: (value: number) => void;
}

const MultiRangeSlider: FC<MultiRangeSliderProps> = ({
  min,
  max,
  points,
  onPointClick,
}) => {
  const [minVal, setMinVal] = useState(min);
  const [maxVal, setMaxVal] = useState(max);
  const [expanded, setExpanded] = useState(false);
  const minValRef = useRef(min);
  const maxValRef = useRef(max);
  const range = useRef<HTMLDivElement>(null);

  const getPercent = useCallback(
    (value: number) => Math.round(((value - min) / (max - min)) * 100),
    [min, max],
  );

  useEffect(() => {
    const minPercent = getPercent(minVal);
    const maxPercent = getPercent(maxValRef.current);

    if (range.current) {
      range.current.style.left = `${minPercent}%`;
      range.current.style.width = `${maxPercent - minPercent}%`;
    }
  }, [minVal, getPercent]);

  useEffect(() => {
    const minPercent = getPercent(minValRef.current);
    const maxPercent = getPercent(maxVal);

    if (range.current) {
      range.current.style.width = `${maxPercent - minPercent}%`;
    }
  }, [maxVal, getPercent]);

  const handlePointClick = (value: number) => {
    // 포인트 클릭 이벤트 핸들러
    if (value > minVal && value < maxVal) {
      // 포인트가 min과 max 사이에 있는 경우에만 업데이트
      onPointClick(value);
      setMinVal(value);
      minValRef.current = value;
    }
  };

  const handleThumbLeftChange = (event: ChangeEvent<HTMLInputElement>) => {
    let value = Math.min(Number(event.target.value), maxVal - 1);
    // 현재 thumbLeft 위치보다 작은 포인트가 있는 경우에 가장 가까운 포인트로 이동
    const closestPoint = points.reduce((closest, point) =>
      Math.abs(point - value) < Math.abs(closest - value) ? point : closest,
    );
    value = closestPoint;
    setMinVal(value);
    minValRef.current = value;
  };

  const handleThumbRightChange = (event: ChangeEvent<HTMLInputElement>) => {
    let value = Math.max(Number(event.target.value), minVal + 1);
    // 현재 thumbRight 위치보다 작은 포인트가 있는 경우에 가장 가까운 포인트로 이동
    const closestPoint = points.reduce((closest, point) =>
      Math.abs(point - value) < Math.abs(closest - value) ? point : closest,
    );
    value = closestPoint;
    setMaxVal(value);
    maxValRef.current = value;
  };

  const handleSliderMouseDown = (event: MouseEvent<HTMLDivElement>) => {
    const slider = range.current;
    if (slider) {
      const sliderRect = slider.getBoundingClientRect();
      const mouseY = event.clientY - sliderRect.top;
      const sliderHeight = sliderRect.height;
      const percent = (mouseY / sliderHeight) * 100;
      const newValue = (percent / 100) * (max - min) + min;

      if (newValue >= min && newValue <= max) {
        setMinVal(newValue);
        minValRef.current = newValue;
        // 확장된 상태로 변경합니다.
        setExpanded(true);
      }
    }
  };

  const handleSliderMouseUp = () => {
    // 슬라이더에서 마우스를 떼면 확장된 상태를 false로 변경하여 원래 높이로 복원합니다.
    setExpanded(false);
  };

  return (
    <s.Container>
      <s.ThumbLeft
        type="range"
        min={min}
        max={max}
        value={minVal}
        onChange={handleThumbLeftChange}
      />
      <s.ThumbRight
        type="range"
        min={min}
        max={max}
        value={maxVal}
        onChange={handleThumbRightChange}
      />

      <s.Slider
        expanded={expanded}
        ref={range}
        onMouseDown={handleSliderMouseDown}
        onMouseUp={handleSliderMouseUp}
      >
        <s.Track />
        <s.Range ref={range} />
        {points.map((point) => (
          <s.Point
            key={point}
            onClick={() => handlePointClick(point)}
            style={{ left: `${getPercent(point)}%` }}
          />
        ))}
        <s.Value style={{ left: `${getPercent(minVal)}%` }}>{minVal}</s.Value>
        <s.Value style={{ left: `${getPercent(maxVal)}%` }}>{maxVal}</s.Value>
      </s.Slider>
    </s.Container>
  );
};

export default MultiRangeSlider;
