import React, { useState } from 'react';
import { format } from 'date-fns';
import TimeRange from '../../components/TimelineSlider/App';

import { selectedInterval, timelineInterval } from './datesSource';

interface AppState {
  error: boolean;
  selectedInterval: Date[];
}

class App extends React.Component<{}, AppState> {
  constructor(props: {}) {
    super(props);
    this.state = {
      error: false,
      selectedInterval,
    };
  }

  errorHandler = ({ error }: { error: boolean }) => {
    this.setState({ error });
  };

  onChangeCallback = (selectedInterval: Date[]) => {
    this.setState({ selectedInterval });
  };

  render() {
    const { selectedInterval, error } = this.state;
    return (
      <div className="container">
        <div className="info">
          <span>Selected Interval: </span>
          {selectedInterval.map((d, i) => (
            <span key={d.getTime()}>{format(d, 'dd MMM, HH:mm')}</span>
          ))}
        </div>

        <TimeRange
          error={error}
          ticksNumber={36}
          selectedInterval={selectedInterval}
          timelineInterval={timelineInterval}
          onUpdateCallback={this.errorHandler}
          onChangeCallback={this.onChangeCallback}
        />
      </div>
    );
  }
}

export default App;
