import React, { useState } from 'react';
import { render } from 'react-dom';
import MultiRangeSlider from '../../components/MultiRangeSlider/MultiRangeSlider';

const Split = () => {
  const min = 0; // 최소값
  const max = 1000; // 최대값
  const initialPoints = [100, 300, 700, 800, 1000]; // 초기 점의 위치

  const [points, setPoints] = useState(initialPoints);

  const handlePointClick = (index: number) => {
    // 클릭한 점을 업데이트
    const newPoints = [...points];
    newPoints[index] += 1000; // 예시로 50 증가
    setPoints(newPoints);
  };

  const handleMouseUp = (): void => {
    // 마우스 업 이벤트 핸들러
    const canvasWrap: HTMLElement | null =
      document.querySelector('.CanvasWrap');
    const rangeSliderWrap: HTMLElement | null =
      document.querySelector('.RangeSliderWrap');
    if (canvasWrap && rangeSliderWrap) {
      canvasWrap.style.height = '60vh';
      rangeSliderWrap.style.height = '40vh';
    }
  };

  const handleMouseDown = (): void => {
    // 마우스 다운 이벤트 핸들러
    const canvasWrap: HTMLElement | null =
      document.querySelector('.CanvasWrap');
    const rangeSliderWrap: HTMLElement | null =
      document.querySelector('.RangeSliderWrap');
    if (canvasWrap && rangeSliderWrap) {
      canvasWrap.style.height = '80vh';
      rangeSliderWrap.style.height = '20vh';
    }
  };

  return (
    <div style={{ paddingTop: '24px' }}>
      <div
        className="CanvasWrap"
        style={{
          width: '80vw',
          height: 'calc(80vh - 24px)',
          margin: '0 auto',
          background: '#fff',
          borderRadius: '20px',
        }}
        onMouseUp={handleMouseUp}
        onMouseDown={handleMouseDown}
      >
        캔버스 영역
      </div>
      <div
        className="RangeSliderWrap"
        style={{
          padding: '16px',
          background: '#fff',
          overflowX: 'scroll',
          height: 'calc(20vh - 24px)',
          marginTop: '24px',
        }}
      >
        <MultiRangeSlider
          min={min}
          max={max}
          points={points}
          onPointClick={handlePointClick}
        />
      </div>
    </div>
  );
};

export default Split;
