import React, { useEffect, useState } from 'react';
import { Route, Routes, useLocation } from 'react-router-dom';
import * as ROUTES from './routes';

import Login from './views/LogIn/LoginForm';

import './App.css';

const App = () => {
  const location = useLocation();

  const connectLiveRoom = async () => {
    const ndp = NDP.instance;
    const { relay } = ndp;

    const rooms = await relay.getRoom();

    if (rooms) {
      const existed = rooms?.resultElements?.filter(
        (e) => e.ownerId === userStore.userData.id,
      );

      if (existed.length) {
        relay.setRoom(existed[0]);
      } else {
        const room = await relay.createRoom();
        if (room.entranceUrl) {
          relay.setRoom(room);
        } else {
          console.error('Create Room Failed. Please check the server status.');
          return;
        }
      }
      await relay.connectRoom();

      await relay.relayWS.join();

      NDP.instance.relay.relayWS.on('UPLOAD_END', listenUploadEnd);
    }
  };

  return (
    // 임시조치

    <Routes>
      {/* Public route */}
      <Route path="/signIn" element={<SignIn />} />
      <Route
        path={NDP_LOGIN_CALLBACK_PATH}
        element={<UserAuthReactElement />}
      />
      <Route path="*" element={<SignIn />} />

      {/* Private route */}
      <Route element={<PrivateRoute />}>
        <Route element={<DefaultLayout />}>
          <Route path="/" element={<Home />} />
          <Route path={ROUTES.MAIN_TIMELINE} element={<TimeLine />} />
          <Route path={ROUTES.MAIN_NOTE_LIST} element={<NoteList />} />
          <Route path={ROUTES.MAIN_PAGE_LIST} element={<PageList />} />
          <Route path={ROUTES.MAIN_TAG} element={<Tag />} />
          <Route path={ROUTES.MAIN_FAVORITE} element={<Favorite />} />
          <Route path={ROUTES.MAIN_RECORD} element={<Record />} />
          <Route path={ROUTES.MAIN_SETTING} element={<Setting />} />
        </Route>
        <Route path={ROUTES.MAIN_PAGE_DETAIL} element={<PageDetail />} />
      </Route>
    </Routes>
  );
};

export default App;
