import React from 'react';
import { format } from 'date-fns';
import TimeRange from '../../components/TimelineSlider/App';
import { selectedInterval, timelineInterval } from './datesSource';

const Split = () => {
  function getRandomDate() {
    const today = new Date();
    const startOfDay = new Date(
      today.getFullYear(),
      today.getMonth(),
      today.getDate(),
      0,
      0,
      0,
    );
    const endOfDay = new Date(
      today.getFullYear(),
      today.getMonth(),
      today.getDate(),
      23,
      59,
      59,
    );

    // 랜덤한 시간 생성
    const randomTime =
      startOfDay.getTime() +
      Math.random() * (endOfDay.getTime() - startOfDay.getTime());

    return new Date(randomTime);
  }
  const randomTimes = [getRandomDate(), getRandomDate(), getRandomDate()];
  return (
    <div>
      <div
        style={{
          width: '80vw',
          /* height: '80vh', */
          margin: '24px auto',
          background: '#fff',
          borderRadius: '20px',
        }}
      >
        캔버스 영역
      </div>
      <div
        style={{
          padding: '16px',
          /* height: '20vh', */
          background: '#fff',
          overflowX: 'scroll',
        }}
      >
        <div className="container">
          <div className="info" style={{ fontSize: '20px' }}>
            {selectedInterval.map((d, i) => (
              <span key={d.getTime()}>{format(d, 'yyyy MM dd, HH:mm')}</span>
            ))}
          </div>

          <TimeRange
            selectedInterval={selectedInterval}
            timelineInterval={timelineInterval}
            randomTimes={rendomTimes}
          />
        </div>
      </div>
    </div>
  );
};

export default Split;
